mapper.select:
select userId, userEmail, userNm, userPhone, userProfile 
 from users 
 where userEmail = #{userEmail}

mapper.update:
update users
 set userPwd = #{userPwd},
 	 userNm = #{userNm},
 	 userPhone = #{userPhone},
 	 userProfile = #{userProfile}  
 where userEmail = #{userEmail}

mapper.select:
select profileUrl 
 from user_profile 
 where userId = ${userId}
